options:
{
	cache:false,
    pattern:{
      mustache:{ token:'{{%logic}}' },
      minimal:{ token:'$%logic' },
      html:{ token:'%ls<%s%logic%s>%le%tmp%ls<%s/%id%s>%le', marker:'html', type:'nested'}
    },
    wildcards: [
      ['%param','%s=%s"%nsList"'],
      ['%nsList','%ns(?:[ ,]%ns)*']
    ],
    marker:{
      'html': Mask.marker(function(){
        this.exp = '(%w) +(?:(id|class)%s=%s"(#%nsList)")';
        this.params = ['tag','selector','params'];

        this.compile = function(template){
          this.id = this.params[2].match(/^#(.*)(?: |$)/)[1];
          this.template = this.opener.replace('#','') + this.template + this.closer;
          console.log(this);
          return this._core('compile');

        };
      })
    }
}

